import { useParams } from "react-router-dom";

export default function SingleUserPage() {
  const { userId } = useParams();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const url = `https://reqres.in/api/users/${id}`;

    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        setUser(data.id);
      });
  });

  return <div>Your markup here</div>;
}
